(100, 1, '2021-04-28 23:15:04', '2021-04-28 20:15:04', '<!-- wp:cover {\"url\":\"http://wp-test-site.loc/wp-content/uploads/2021/04/other-small.jpg\",\"id\":99,\"dimRatio\":0} -->\n			<div class=\"wp-block-cover\" style=\"background-image:url(http://wp-test-site.loc/wp-content/uploads/2021/04/other-small.jpg)\"><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"placeholder\":\"Write title…\",\"textColor\":\"white\",\"fontSize\":\"large\"} -->\n			<p class=\"has-text-align-center has-large-font-size\">Welcome to the store</p>\n			<!-- /wp:paragraph -->\n\n			<!-- wp:paragraph {\"align\":\"center\",\"textColor\":\"white\"} -->\n			<p class=\"has-text-color has-text-align-center\">Write a short welcome message here</p>\n			<!-- /wp:paragraph -->\n\n			<!-- wp:button {\"align\":\"center\"} -->\n			<div class=\"wp-block-button aligncenter\"><a href=\"http://wp-test-site.loc/?page_id=41\" class=\"wp-block-button__link\">Go shopping</a></div>\n			<!-- /wp:button --></div></div>\n			<!-- /wp:cover -->\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">Shop by Category</h2>\n				<!-- /wp:heading -->\n				<!-- wp:shortcode -->\n				[product_categories number=\"0\" parent=\"0\"]\n				<!-- /wp:shortcode -->\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">New In</h2>\n				<!-- /wp:heading -->\n				<!-- wp:woocommerce/product-new {\"columns\":4} /-->\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">Fan Favorites</h2>\n				<!-- /wp:heading -->\n				<!-- wp:woocommerce/product-top-rated {\"columns\":4} /-->\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">On Sale</h2>\n				<!-- /wp:heading -->\n				<!-- wp:woocommerce/product-on-sale {\"columns\":4} /-->\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">Best Sellers</h2>\n				<!-- /wp:heading -->\n				<!-- wp:woocommerce/product-best-sellers {\"columns\":4} /-->\n			', 'Homepage', '', 'inherit', 'closed', 'closed', '', '98-revision-v1', '', '', '2021-04-28 23:15:04', '2021-04-28 20:15:04', '', 98, 'http://wp-test-site.loc/?p=100', 0, 'revision', '', 0),
